import { createActionGroup, emptyProps, props } from '@ngrx/store';
import { Question, category } from '../quiz/quiz.interface';

export const QuizActions = createActionGroup({
  source: 'Quiz',
  events: {
    'Load Questions': emptyProps(),

    'Set Current Question Index': props<{ index: number }>(),

    'Select Option': props<{ option: string }>(),

    'Next Question': emptyProps(),

    'Skip Question': emptyProps(),

    'Previous Question': emptyProps(),

    'Update Score': props<{ score: number }>(),

    'Restart Quiz': emptyProps(),

    'Load Categories': emptyProps(),
  },
});
export const QuizApiActions = createActionGroup({
  source: 'Quiz/API',
  events: {
    'Load Questions Success': props<{ questions: Question[] }>(),

    'Load Questions Failure': props<{ error: any }>(),

    'Load Categories Success': props<{ categories: category }>(),

    'Load Categories Failure': props<{ error: any }>(),
  },
});
