import { createAction, props } from '@ngrx/store';
import { QuizAppEntity } from './quiz-app.models';

export const initQuizApp = createAction('[QuizApp Page] Init');

export const loadQuizAppSuccess = createAction(
  '[QuizApp/API] Load QuizApp Success',
  props<{ quizApp: QuizAppEntity[] }>()
);

export const loadQuizAppFailure = createAction(
  '[QuizApp/API] Load QuizApp Failure',
  props<{ error: any }>()
);
