          type="radio"
          type="radio"
          name="options"
          id="{{ 'option' + i }}"
        />
        <label for="">{{ option }}</label>
      </div>
      <div class="btn-flex">
        <button
          class="next-btn"
          (click)="previousQuestion()"
          [disabled]="isFirstQuestion$ | async"
        >
          Previous
        </button>
        <button class="next-btn" (click)="skipQuestion()">Skip</button>
        <button
          class="next-btn"
          (click)="nextQuestion()"
          [disabled]="!isOptionSelected"
        >
          {{ nextBtn }}
        </button>
      </div>
    </div>
  </div>
</section>
