import { Component, OnInit } from '@angular/core';
import { Quiz } from './quiz.interface';
import { QuizReduxService } from '../quiz-redux.service';

@Component({
  selector: 'quiz-app-quiz',
  templateUrl: './quiz.component.html',
  styleUrls: ['./quiz.component.scss'],
})
export class QuizComponent implements OnInit {
  // "category": "Society & Culture",
  //       "id": "626288684b176d54800e3d9a",
  //       "correctAnswer": "BMW",
  //       "incorrectAnswers": [
  //           "Harley Davidson",
  //           "Mercedez-Benz",
  //           "Porsche"
  //       ],
  //       "question": "Which motoring brand is associated with the marketing slogan 'The Ultimate Driving Machine'?",
  //       "tags": [
  //           "business",
  //           "marketing",
  //           "motoring",
  //           "society_and_culture"
  //       ],
  //       "type": "Multiple Choice",
  //       "difficulty": "medium",
  //       "regions": [],
  //       "isNiche": false
  state: Quiz = {
    totalQuestions: 0,
    questionNo: 0,
    current_score: 0,
    total_score: 0,
    questions: [],
    options: [],
    currentQuestion: '',
    current_Question_Index: 0,
  };
  constructor(private triviaService: QuizReduxService) {}

  ngOnInit(): void {
    this.fetchTriviaQuestion();
  }

  fetchTriviaQuestion() {
    this.triviaService.getTriviaQuestion().subscribe((triviaResponse: any) => {
      if (triviaResponse) {
        this.state.totalQuestions = triviaResponse.length;
        this.state.total_score = triviaResponse.length;

        this.state.questions = triviaResponse.map((questionItem: any) => {
          const incorrectAnswers = questionItem.incorrectAnswers;
          const correctAnswer = questionItem.correctAnswer;

          return {
            question: questionItem.question.text,
            options: [...incorrectAnswers, correctAnswer],
          };
        });

        // Set the initial question
        this.setCurrentQuestion(0);
      } else {
        console.error('Invalid response structure');
      }
    });
  }
  setCurrentQuestion(index: number) {
    if (index >= 0 && index < this.state.totalQuestions) {
      this.state.current_Question_Index = index;
      this.state.currentQuestion = this.state.questions[index].question;
      this.state.options = this.state.questions[index].options;
    }
  }
  nextQuestion() {
    const nextIndex = this.state.current_Question_Index + 1;
    this.setCurrentQuestion(nextIndex);
  }
}
<!-- QUIZ ONE -->
<section class="section-1" id="section-1">
  <main>
    <h3 class="text-container">Redux Angualr Quiz</h3>
    <div class="flex">
      <p>{{ state.current_Question_Index + 1 }}/{{ state.questions.length }}</p>
      <p class="score-text">
        Score: {{ state.current_score }}/{{ state.total_score }}
      </p>
    </div>
    <p class="question-style">{{ state.currentQuestion }}</p>
    <div class="quiz-options">
      <button class="" *ngFor="let option of state.options; let i = index">
        <span class="alphabet">{{ i + 1 }}</span>
        {{ option }}
      </button>
    </div>
    <div class="btn-flex">
      <button class="cancel-btn" id="cancel-btn" type="submit">Cancel</button>
      <button class="next-btn" id="btn" type="submit" (click)="nextQuestion()">
        Next
      </button>
    </div>
  </main>
</section>
