import { Component, OnInit } from '@angular/core';
import { Question, Quiz } from './quiz.interface';
import { QuizReduxService } from '../quiz-redux.service';
import { Store, select } from '@ngrx/store';
import * as QuizActions from '../+state/quiz-app.actions';
import { quizAppReducer } from '../+state/quiz-app.reducer';
import {
  Observable,
  combineLatest,
  filter,
  map,
  take,
  withLatestFrom,
} from 'rxjs';
import { Router } from '@angular/router';
import {
  selectCorrectAnswer,
  selectCurrentQuestion,
  selectCurrentQuestionIndex,
  selectCurrentScore,
  selectQuestions,
  selectTotalQuestions,
} from '../+state/quiz-app.selectors';

@Component({
  selector: 'quiz-app-quiz',
  templateUrl: './quiz.component.html',
  styleUrls: ['./quiz.component.scss'],
})
export class QuizComponent implements OnInit {
  questions$!: Observable<Question[]>;
  currentQuestionIndex$!: Observable<number | null>;
  totalQuestions$!: Observable<number | null>;
  currentQuestion$!: Observable<Question | undefined>;
  correctAnswer$!: Observable<string>;
  currentScore$!: Observable<number>;
  isFirstQuestion$!: Observable<boolean>;
  isLastQuestion$!: Observable<boolean>;
  isOptionSelected = false;
  quizText!: string;
  selectedOption: string | null = null;
  selectedOptionClass: string | null = null;
  correctAnswerClass: string | null = null;
  // state: Quiz = {
  //   totalQuestions: 0,
  //   current_score: 0,
  //   total_score: 0,
  //   questions: [],
  //   options: [],
  //   currentQuestion: '',
  //   current_Question_Index: 0,
  // };
  selectedResponses: { option: string; isCorrect: boolean }[] = [];

  constructor(private store: Store, private router: Router) {}

  ngOnInit(): void {
    this.store.dispatch(QuizActions.loadQuestions());
    this.currentQuestionIndex$ = this.store.pipe(
      select(selectCurrentQuestionIndex),
      map((index) => index + 1)
    );
    this.totalQuestions$ = this.store.pipe(select(selectTotalQuestions));
    this.currentQuestion$ = this.store.pipe(select(selectCurrentQuestion));
    this.totalQuestions$ = this.store.pipe(select(selectTotalQuestions));
    this.correctAnswer$ = this.store.pipe(select(selectCorrectAnswer));
    this.currentScore$ = this.store.pipe(select(selectCurrentScore));
    // for previous question
    this.isFirstQuestion$ = this.currentQuestionIndex$.pipe(
      map((index) => index === 1)
    );

    this.isLastQuestion$ = combineLatest([
      this.currentQuestionIndex$,
      this.totalQuestions$,
    ]).pipe(
      map(([index, totalQuestions]) => {
        console.log('Total Questions:', totalQuestions);
        const lastQuestion = (index ?? 0) > (totalQuestions ?? 0);
        return lastQuestion;
      })
    );

    //this is only to show in console and otherwise not working at all
    this.correctAnswer$.subscribe((correctAnswer) =>
      console.log('Correct Answer:', correctAnswer)
    );
    this.currentQuestionIndex$.subscribe((index) => {
      console.log('Current Question Index:', index);
    });
  }

  // setCurrentQuestion(index: number) {
  //   this.quizText = 'Redux Angular Quiz';
  //   this.store.dispatch(QuizActions.setCurrentQuestionIndex({ index }));
  // }

  skipQuestion() {
    console.log('skip');
    this.store.dispatch(QuizActions.skipQuestion());
  }
  nextQuestion() {
    this.store.dispatch(QuizActions.nextQuestion());
    // reset styling
    this.selectedOption = null;
    this.selectedOptionClass = null;
    this.correctAnswerClass = null;
    this.isOptionSelected = false;

    this.currentQuestionIndex$.subscribe((index) => {
      if (index && index > 10) {
        this.router.navigate(['/result']);
      }
    });
  }
  previousQuestion() {
    this.store.dispatch(QuizActions.previousQuestion());
  }
  isCorrectAnswer(option: string): boolean {
    let correctAnswer: string | undefined;
    this.store
      .pipe(select(selectCurrentQuestion))
      .subscribe((currentQuestion) => {
        if (currentQuestion) {
          correctAnswer = currentQuestion.correctAnswer;
        }
      });

    return !!correctAnswer && correctAnswer === option;
  }

  // Function to handle option selection
  selectOption(option: string) {
    if (!this.selectedOption) {
      this.selectedOption = option;
      const isCorrect = this.isCorrectAnswer(option);
      this.store.dispatch(QuizActions.selectOption({ option, isCorrect }));
      if (isCorrect) {
        this.store.dispatch(QuizActions.updateScore({ score: 1 }));
      }

      this.selectedOptionClass = isCorrect
        ? 'correct-answer'
        : 'incorrect-answer';
      this.correctAnswerClass = 'correct-answer';
      this.isOptionSelected = true;
    }
  }
}
<section class="section-1" id="section-1">
  <main>
    <ng-container>
      <div
        *ngIf="{
          currentQuestion: currentQuestion$ | async,
          totalQuestions: totalQuestions$ | async
        } as data"
      >
        <div *ngIf="data.currentQuestion">
          <h3 class="text-container">Redux Angular Quiz</h3>
          <div class="flex">
            <p>
              {{ currentQuestionIndex$ | async }}/{{ totalQuestions$ | async }}
            </p>
            <p>
              Score: {{ currentScore$ | async }}/{{ totalQuestions$ | async }}
            </p>
          </div>

          <p class="question-style">{{ data.currentQuestion.question }}</p>
          <div class="quiz-options">
            <button
              class="option-btn"
              [ngClass]="{
                'selected-option': selectedOption === option,
                'correct-answer':
                  correctAnswerClass === 'correct-answer' &&
                  isCorrectAnswer(option)
              }"
              *ngFor="let option of data.currentQuestion.options; let i = index"
              (click)="selectOption(option)"
            >
              <span class="alphabet">{{ i + 1 }}</span>
              {{ option }}
            </button>
          </div>
          <div class="btn-flex">
            <button
              class="next-btn"
              (click)="previousQuestion()"
              [disabled]="isFirstQuestion$ | async"
            >
              Previous
            </button>
            <button class="next-btn" (click)="skipQuestion()">Skip</button>
            <button
              class="next-btn"
              (click)="nextQuestion()"
              [disabled]="!isOptionSelected"
            >
              Next
            </button>
          </div>
        </div>
      </div>
    </ng-container>
  </main>
</section>
