import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable, catchError, map, tap, throwError } from 'rxjs';
import { Question, Quiz } from './quiz/quiz.interface';

@Injectable({
  providedIn: 'root',
})
export class QuizReduxService {
  private apiUrl = 'https://the-trivia-api.com/v2/questions';

  constructor(private http: HttpClient) {}

  private mapApiResponseToQuiz(apiResponse: any): Quiz {
    return {
      totalQuestions: 1, // Assuming each API call returns one question
      current_score: 0,
      total_score: 1,
      current_Question_Index: 0,
      currentQuestion: apiResponse.question.text,
      questions: [
        {
          question: apiResponse.question.text,
          options: this.shuffleArray([
            ...apiResponse.incorrectAnswers,
            apiResponse.correctAnswer,
          ]),
          correctAnswer: apiResponse.correctAnswer,
        },
      ],
      options: [], // Empty options array initially
    };
  }

  getTriviaQuestion(): Observable<Quiz> {
    return this.http.get<any>(this.apiUrl).pipe(
      tap((apiResponse) => console.log('API Response:', apiResponse)),
      map((apiResponse) => this.mapApiResponseToQuiz(apiResponse)),
      catchError((error) => {
        console.error('Error fetching trivia question:', error);
        return throwError(error);
      })
    );
  }

  private shuffleArray(array: string[]): string[] {
    return array.sort(() => Math.random() - 0.6);
  }
}
