import { EntityState, EntityAdapter, createEntityAdapter } from '@ngrx/entity';
import { createReducer, on, Action } from '@ngrx/store';

import * as QuizAppActions from './quiz-app.actions';
import { QuizAppEntity } from './quiz-app.models';

export const QUIZ_APP_FEATURE_KEY = 'quizApp';

export interface QuizAppState extends EntityState<QuizAppEntity> {
  selectedId?: string | number; // which QuizApp record has been selected
  loaded: boolean; // has the QuizApp list been loaded
  error?: string | null; // last known error (if any)
}

export interface QuizAppPartialState {
  readonly [QUIZ_APP_FEATURE_KEY]: QuizAppState;
}

export const quizAppAdapter: EntityAdapter<QuizAppEntity> =
  createEntityAdapter<QuizAppEntity>();

export const initialQuizAppState: QuizAppState = quizAppAdapter.getInitialState(
  {
    // set initial required properties
    loaded: false,
  }
);

const reducer = createReducer(initialQuizAppState);

export function quizAppReducer(
  state: QuizAppState | undefined,
  action: Action
) {
  return reducer(state, action);
}
