<section class="section-1" id="section-1">
  <main>
    <div *ngIf="state.current_Question_Index <= state.totalQuestions - 1">
      <h3 class="text-container">{{ quizText }}</h3>
      <div class="flex">
        <p>
          {{ state.current_Question_Index + 1 }}/{{ state.questions.length }}
        </p>
        <p class="score-text">
          Score: {{ state.current_score }}/{{ state.total_score }}
        </p>
      </div>
      <p class="question-style">{{ state.currentQuestion }}</p>
      <div class="quiz-options">
        <button
          class="option-btn"
          *ngFor="let option of state.options; let i = index"
          [ngClass]="{
            'selected-option': selectedOption === option,
            'correct-answer':
              selectedOptionClass === 'correct-answer' &&
              selectedOption === option,
            'incorrect-answer':
              selectedOptionClass === 'incorrect-answer' &&
              selectedOption === option,
            'correct-answer-background':
              correctAnswerClass === 'correct-answer' && isCorrectAnswer(option)
          }"
          (click)="selectOption(option)"
          [disabled]="isOptionSelected"
        >
          <span class="alphabet">{{ i + 1 }}</span>
          {{ option }}
        </button>
      </div>
      <div class="btn-flex">
        <button class="cancel-btn" id="cancel-btn" type="submit">Cancel</button>
        <button
          class="next-btn"
          id="btn"
          type="submit"
          (click)="nextQuestion()"
          [disabled]="!isOptionSelected"
        >
          Next
        </button>
      </div>
    </div>
  </main>
</section>
