import { EntityState, EntityAdapter, createEntityAdapter } from '@ngrx/entity';
import { createReducer, on } from '@ngrx/store';

import { QuizActions } from './quiz-app.actions';
import { QuizApiActions } from './quiz-app.actions';
import { QuizAppEntity } from './quiz-app.models';
import { Quiz } from '../quiz/quiz.interface';

export const QUIZ_APP_FEATURE_KEY = 'quizApp';

export const initialState: Quiz = {
  totalQuestions: 0,
  current_score: 0,
  total_score: 0,
  questions: [],
  options: [],
  currentQuestion: '',
  current_Question_Index: 0,
  selectedOption: '',
  isOptionSelected: false,
  selectedOptionClass: '',
  correctAnswerClass: '',
  userResponses: [],
  isFirstQuestion$: false,
  correctAnswer: '',
};

export const quizAppReducer = createReducer(
  initialState,
  // on(QuizActions.loadQuestionsSuccess, (state, { questions }) => ({
  //   ...state,
  //   questions,
  // })),
  on(QuizApiActions.loadQuestionsSuccess, (state, { questions }) => ({
    ...state,
    questions: questions,
    totalQuestions: questions.length,
    currentQuestion: questions[state.current_Question_Index].question.text,
    options: questions[state.current_Question_Index].incorrectAnswers
      .concat(questions[state.current_Question_Index].correctAnswer)
      .sort(),
  })),

  on(QuizActions.nextQuestion, (state) => {
    const nextIndex = state.current_Question_Index + 1;

    return {
      ...state,
      current_Question_Index: nextIndex,
      currentQuestion: state.questions[nextIndex]?.question.text,
    };
  }),
  on(QuizActions.skipQuestion, (state) => {
    const nextIndex = state.current_Question_Index + 1;

    return {
      ...state,
      current_Question_Index: nextIndex,
      currentQuestion: state.questions[nextIndex]?.question.text,
    };
  }),
  on(QuizActions.previousQuestion, (state) => {
    const previousIndex = state.current_Question_Index - 1;
    // const nextIndex = state.current_Question_Index + 1;

    return {
      ...state,
      current_Question_Index: previousIndex,
      currentQuestion: state.questions[previousIndex]?.question.text,
    };
  }),

  on(QuizActions.updateScore, (state, { score }) => {
    return { ...state, current_score: state.current_score + score };
  }),

  on(QuizActions.restartQuiz, () => initialState)
);
