  constructor(
    private store: Store<Quiz>,
    private router: Router,
    private quizService: QuizReduxService
  ) {}

  quizStarted = false;
  // currentQuestionNumber!: number;
  currentQuestionNumber$!: Observable<number | null>;
  isFirstQuestion$!: Observable<boolean>;
  totalQuestions!: number;
  score = 0;
  isOptionSelected = false;
  lastQuestion$!: Observable<boolean>;
  previousAllowed = true;
  answered = false;
  selectedOption: string | undefined;
  showFooter = true;
  // questions!: Question[];
  questions$!: Observable<Question[]>;
  finalScoreMessage: string | null = null;
  showFinalScore = false;
  selectedButton = false;
  correctAnswer!: string;
  imgOptions: any[] = [];
  choice: any;
  types!: string;
  optionWindowVisible = false;
  sideWindowVisible = false;
  response!: string;
  triviaState$!: Observable<Quiz>;
  selectCurrentQuestion$!: Observable<Question>;
  nextBtn = 'Next';

  ngOnInit(): void {
    this.store.dispatch(QuizActions.loadTrivia());

    this.questions$ = this.store.pipe(select(selectQuestions));
    this.selectCurrentQuestion$ = this.store.pipe(
      select(selectCurrentQuestion)
    );
    this.triviaState$ = this.store.select(selectTriviaState);
    this.currentQuestionNumber$ = this.store.pipe(
      select(selectCurrentQuestionNumber)
    );
    this.lastQuestion$ = this.store.select('lastQuestion');
  }

  openSideWindow() {
    this.sideWindowVisible = true;
  }
  closeSideWindow() {
    this.sideWindowVisible = false;
  }
  toggleOptionWindow() {
    this.optionWindowVisible = !this.optionWindowVisible;
  }

  setCurrentQuestion(index: number) {
    this.store.dispatch(
      QuizActions.setCurrentQuestion({ currentQuestionNumber: index + 1 })
    );
  }

  triviaSubscribe() {
    this.store.dispatch(QuizActions.loadTrivia());
  }

  // Replace nextQuestion, skipQuestion, and handleOption methods
  nextQuestion(): void {
    this.store.dispatch(QuizActions.nextQuestion());
    this.isOptionSelected = false;
    this.currentQuestionNumber$.subscribe((index) => {
      if (index && index > this.totalQuestions) {
        this.router.navigate(['/result']);
      }
    });
  }

  skipQuestion() {
    this.store.dispatch(QuizActions.skipQuestion());
  }

  handleOption(guess: string) {
    this.store.dispatch(QuizActions.answerQuestion({ guess }));
    this.isOptionSelected = true;
  }

  // Restart quiz method
  restartQuiz() {
    this.triviaSubscribe();
    this.store.dispatch(QuizActions.restartQuiz());
  }
  previousQuestion() {
    this.store.dispatch(QuizActions.previousQuestion());
  }
}
