<section *ngIf="triviaState$ | async as trivia">
  <div class="side-btn">
    <button (click)="openSideWindow()">
      <i class="fa fa-angle-right"></i>
    </button>
  </div>
  <div (click)="closeSideWindow()">
    <div *ngIf="sideWindowVisible" class="side-window-container">
      <div class="side-window" (click)="$event.stopPropagation()">
        <div
          *ngFor="let question of trivia.questions; let i = index"
          class="window-flex"
        >
          <h3 (click)="setCurrentQuestion(i)">
            {{ question.question.text }}
          </h3>
          <div class="attempted-question">
            <i class="fa fa-check-circle"></i>
          </div>
        </div>
      </div>
    </div>
  </div>

  <div class="nav" *ngIf="triviaState$ | async as trivia">
    <div class="navbar">
      <button
        class="icon-button"
        (click)="previousQuestion()"
        [disabled]="!previousAllowed"
      >
        <i class="fa fa-angle-left"></i>
      </button>
      <button class="ques-btn">
        Question {{ trivia.currentQuestionNumber }}/{{ trivia.totalQuestions }}
      </button>
      <button class="icon-button" (click)="nextQuestion()">
        <i class="fa fa-angle-right"></i>
      </button>

      <button class="option-panel" (click)="toggleOptionWindow()">
        <i class="fas fa-check-square"></i>
      </button>
    </div>
  </div>
  <div class="container">
    <div class="quiz-flex">
      <h3>
        {{ (selectCurrentQuestion$ | async)?.question?.text }}
      </h3>
      <ul>
        <li *ngFor="let option of trivia.options; let i = index">
          {{ option }}
        </li>
      </ul>
    </div>
  </div>
  <div *ngIf="optionWindowVisible" class="option-window">
    <div class="radio-btns">
      <div *ngFor="let option of trivia.options; let i = index">
        <input
          type="radio"
          type="radio"
          name="options"
          id="{{ 'option' + i }}"
          (click)="handleOption(option)"
          [disabled]="response"
        />
        <label class="correct" for="">{{ option }}</label>
      </div>
      <div class="btn-flex">
        <button
          class="next-btn"
          (click)="previousQuestion()"
          [disabled]="!previousAllowed"
        >
          Previous
        </button>
        <button class="next-btn" (click)="nextQuestion()">
          {{ nextBtn }}
        </button>
      </div>
    </div>
  </div>
</section>
