import { Component, OnInit } from '@angular/core';
import { Quiz } from './quiz.interface';
import { QuizReduxService } from '../quiz-redux.service';

@Component({
  selector: 'quiz-app-quiz',
  templateUrl: './quiz.component.html',
  styleUrls: ['./quiz.component.scss'],
})
export class QuizComponent implements OnInit {
  // "category": "Society & Culture",
  //       "id": "626288684b176d54800e3d9a",
  //       "correctAnswer": "BMW",
  //       "incorrectAnswers": [
  //           "Harley Davidson",
  //           "Mercedez-Benz",
  //           "Porsche"
  //       ],
  //       "question": "Which motoring brand is associated with the marketing slogan 'The Ultimate Driving Machine'?",
  //       "tags": [
  //           "business",
  //           "marketing",
  //           "motoring",
  //           "society_and_culture"
  //       ],
  //       "type": "Multiple Choice",
  //       "difficulty": "medium",
  //       "regions": [],
  //       "isNiche": false
  state: Quiz = {
    totalQuestions: 10,
    questionNo: 1,
    current_score: 0,
    total_score: 10,
    question: 'What does CSS stand for?',
    options: [
      'Cascading Style Sheets',
      'Creative Style Sheets',
      'Cascading Style Screen',
      'Cascading Stop Sheets',
    ],
  };
  constructor(private triviaService: QuizReduxService) {}

  ngOnInit(): void {
    this.fetchTriviaQuestion();
  }

  fetchTriviaQuestion() {
    this.triviaService.getTriviaQuestion().subscribe((triviaQuestion: Quiz) => {
      console.log('triviaQuestion:', triviaQuestion[0]);
      this.state.question = triviaQuestion.question;
      this.state.options = triviaQuestion.options;
    });
  }
}
<!-- QUIZ ONE -->
<section class="section-1" id="section-1">
  <main>
    <h3 class="text-container">Redux Angualr Quiz</h3>
    <div class="flex">
      <p>{{ state.questionNo }}/{{ state.totalQuestions }}</p>
      <p class="score-text">
        Score: {{ state.current_score }}/{{ state.total_score }}
      </p>
    </div>
    <p class="question-style">{{ state.question }}</p>
    <form>
      <div class="quiz-options">
        <button class="" *ngFor="let option of state.options; let i = index">
          <span class="alphabet">{{ i + 1 }}</span>
          {{ option }}
        </button>
      </div>
      <button id="btn" type="submit">Next</button>
    </form>
  </main>
</section>
