import { Component } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { QuizReduxService } from '../quiz-redux.service';
import { QuizActions } from '../+state/quiz-app.actions';

import { Store } from '@ngrx/store';
import { Observable } from 'rxjs';
import { Category } from '../quiz/quiz.interface';
import {
  selectCategories,
  selectTotalQuestions,
} from '../+state/quiz-app.selectors';

@Component({
  selector: 'quiz-app-user-login',
  templateUrl: './user-login.component.html',
  styleUrls: ['./user-login.component.scss'],
})
export class UserLoginComponent {
  quizLoginForm!: FormGroup;
  categories$!: Observable<Category>;
  totalQuestions$!: Observable<number>;
  constructor(
    private store: Store,
    private fb: FormBuilder,
    private router: Router
  ) {}

  ngOnInit() {
    this.store.dispatch(QuizActions.loadCategories());
    this.store.dispatch(QuizActions.loadQuestions());
    this.store.select(selectCategories).subscribe((categories) => {
      console.log('Categories received in component:', categories);
    });

    this.totalQuestions$ = this.store.select(selectTotalQuestions);
    this.categories$ = this.store.select(selectCategories);
    this.store.dispatch(QuizActions.loadCategories());
    this.quizLoginForm = this.fb.group({
      username: ['', [Validators.required]],
      numberOfQuestions: [0, [Validators.required, Validators.min(1)]],
      difficulty: ['', [Validators.required]],
      category: ['', [Validators.required]],
    });
  }

  onSubmit() {
    const selectedNumberOfQuestions =
      this.quizLoginForm.get('numberOfQuestions')?.value;
    const selectedCategory = this.quizLoginForm.get('category')?.value;
    const selectedDifficulty = this.quizLoginForm.get('difficulty')?.value;

    this.store.dispatch(
      QuizActions.setSelectedNumberOfQuestions({ selectedNumberOfQuestions })
    );
    this.store.dispatch(QuizActions.setSelectedCategory({ selectedCategory }));
    this.store.dispatch(
      QuizActions.setSelectedDifficulty({ selectedDifficulty })
    );

    console.log('form submit', this.quizLoginForm.value);
    this.quizLoginForm.reset();
    this.router.navigate(['/quizstart']);
  }
}
<div class="container">
  <div class="result">
    <h1>User Login</h1>
    <form [formGroup]="quizLoginForm" (ngSubmit)="onSubmit()">
      <label for="username">Username:</label>
      <input
        placeholder="Enter Username"
        type="text"
        id="username"
        formControlName="username"
      />

      <div>
        <label for="totalQuestions">Total Questions:</label>
        <input
          type="range"
          id="numberOfQuestions"
          formControlName="numberOfQuestions"
          min="1"
          max="10"
        />
        <span>{{ quizLoginForm.get('numberOfQuestions')?.value }}</span>
      </div>
      <div>
        <label for="difficulty">Difficulty:</label>
        <select id="difficulty" formControlName="difficulty">
          <option selected disabled value="" selected>
            Select Difficulty level
          </option>
          <option value="easy">Easy</option>
          <option value="medium">Medium</option>
          <option value="hard">Hard</option>
        </select>
      </div>

      <!-- <div>
        <label for="category">Category:</label>
        <select formControlName="category" id="category" class="form-control">
          <option selected disabled value="" selected>Select Category</option>
          <option *ngFor="let categoryKey of categories$ | async | keyvalue">
            {{ categoryKey.key }}
          </option>
        </select>
      </div> -->
      <div>
        <ng-select formControlName="category">
          <ng-option
            *ngFor="let categoryKey of categories$ | async | keyvalue"
            >{{ categoryKey.key }}</ng-option
          >
        </ng-select>
      </div>

      <div class="quizSubmit">
        <button type="submit" [disabled]="quizLoginForm.invalid">Submit</button>
      </div>
    </form>
  </div>
</div>
