import { Injectable, inject } from '@angular/core';
import { createEffect, Actions, ofType } from '@ngrx/effects';
import { switchMap, catchError, of } from 'rxjs';
import * as QuizAppActions from './quiz-app.actions';
import * as QuizAppFeature from './quiz-app.reducer';

@Injectable()
export class QuizAppEffects {
  private actions$ = inject(Actions);

  init$ = createEffect(() =>
    this.actions$.pipe(
      ofType(QuizAppActions.loadQuestions),
      switchMap(() =>
        of(QuizAppActions.loadQuestionsSuccess({ questions: [] }))
      ),
      catchError((error) => {
        console.error('Error', error);
        return of(QuizAppActions.loadQuestionsFailure({ error }));
      })
    )
  );
}
