<section
  *ngIf="{
    currentQuestion: currentQuestion$ | async,
    totalQuestions: totalQuestions$ | async
  } as data"
>
  <div class="side-btn">
    <button (click)="openSideWindow()">
      <i class="fa fa-angle-right"></i>
    </button>
  </div>
  <div (click)="closeSideWindow()">
    <div *ngIf="sideWindowVisible" class="side-window-container">
      <div class="side-window" (click)="$event.stopPropagation()">
        <div
          *ngFor="let question of questions$ | async; let i = index"
          class="window-flex"
        >
          <h3 (click)="setCurrentQuestion(question, i)">
            {{ question.question.text }}
          </h3>
          <div class="attempted-question">
            <i class="fa fa-check-circle"></i>
          </div>
        </div>
      </div>
    </div>
  </div>

  <div class="nav">
    <div class="navbar">
      <button
        class="icon-button"
        (click)="previousQuestion()"
        [disabled]="isFirstQuestion$ | async"
      >
        <i class="fa fa-angle-left"></i>
      </button>
      <button class="ques-btn">
        Question {{ currentQuestionIndex$ | async }}/{{
          totalQuestions$ | async
        }}
      </button>
      <button class="icon-button" (click)="nextQuestion()">
        <i class="fa fa-angle-right"></i>
      </button>

      <button class="option-panel" (click)="toggleOptionWindow()">
        <i class="fas fa-check-square"></i>
      </button>
    </div>
  </div>
  <div class="container">
    <div class="quiz-flex">
      <h3>
        {{ data.currentQuestion?.question?.text }}
      </h3>
      <!-- <h3>
        {{ currentQuestion?.question?.text }}
      </h3> -->
      <ul>
        <li *ngFor="let option of data.currentQuestion?.options; let i = index">
          {{ option }}
        </li>
      </ul>
    </div>
  </div>
  <div *ngIf="optionWindowVisible" class="option-window">
    <div class="radio-btns">
      <div *ngFor="let option of data.currentQuestion?.options; let i = index">
        <input
          type="radio"
          type="radio"
          name="options"
          id="{{ 'option' + i }}"
          (click)="selectOption(option)"
        />
        <label [ngClass]="getOptionClasses(option) | async" for="">{{
          option
        }}</label>
      </div>
      <div class="btn-flex">
        <button
          class="next-btn"
          (click)="previousQuestion()"
          [disabled]="isFirstQuestion$ | async"
        >
          Previous
        </button>
        <button
          class="next-btn"
          (click)="nextQuestion()"
          [disabled]="(isOptionSelected$ | async) === false"
        >
          {{ nextBtn }}
        </button>
      </div>
    </div>
  </div>
</section>
