import { EntityState, EntityAdapter, createEntityAdapter } from '@ngrx/entity';
import { createReducer, on } from '@ngrx/store';

import { QuizActions } from './quiz-app.actions';
import { QuizAppEntity } from './quiz-app.models';
import { Quiz } from '../quiz/quiz.interface';

export const QUIZ_APP_FEATURE_KEY = 'quizApp';

export const initialState: Quiz = {
  totalQuestions: 0,
  current_score: 0,
  total_score: 0,
  questions: [],
  options: [],
  currentQuestion: '',
  current_Question_Index: 0,
  selectedOption: '',
  isOptionSelected: false,
  selectedOptionClass: '',
  correctAnswerClass: '',
  userResponses: [],
  isFirstQuestion$: false,
  correctAnswer: '',
};

export const quizAppReducer = createReducer(
  initialState,
  on(QuizActions.loadQuestionsSuccess, (state, { questions }) => ({
    ...state,
    questions,
  })),

  on(QuizActions.selectOption, (state, { option }) => {
    const userResponses = [...state.userResponses];
    userResponses[state.current_Question_Index] = option;

    return {
      ...state,
      selectedOption: option,
      isOptionSelected: true,
      selectedOptionClass:
        state.questions[state.current_Question_Index].correctAnswer === option
          ? 'correct-answer'
          : 'incorrect-answer',
      correctAnswerClass: 'correct-answer',
      userResponses,
    };
  }),

  on(QuizActions.nextQuestion, (state) => {
    const nextIndex = state.current_Question_Index + 1;

    return {
      ...state,
      current_Question_Index: nextIndex,
      currentQuestion: state.questions[nextIndex]?.question,
      options: state.questions[nextIndex]?.options,
    };
  }),
  on(QuizActions.skipQuestion, (state) => {
    const nextIndex = state.current_Question_Index + 1;

    return {
      ...state,
      current_Question_Index: nextIndex,
      currentQuestion: state.questions[nextIndex]?.question,
      options: state.questions[nextIndex]?.options,
    };
  }),
  on(QuizActions.previousQuestion, (state) => {
    const newIndex = state.current_Question_Index - 1;
    // add
    if (state.current_Question_Index > 1) {
      const previousQuestionIndex = state.current_Question_Index - 1;
      const previousQuestion = state.questions[previousQuestionIndex];
      console.log(previousQuestionIndex);
      return {
        ...state,
        current_Question_Index: state.current_Question_Index - 1,
        currentQuestion: previousQuestion.question,
        options: state.questions[newIndex]?.options,
        selectedOption: state.userResponses[previousQuestionIndex] || null,
      };
    } else {
      console.log('previous allowed');
      return { ...state, isFirstQuestion$: true };
    }
    // return { ...state, current_Question_Index: newIndex };
  }),

  on(QuizActions.updateScore, (state, { score }) => {
    return { ...state, current_score: state.current_score + score };
  }),

  on(QuizActions.restartQuiz, () => initialState)
);
