import { Component, OnInit } from '@angular/core';
import { Question, Quiz } from './quiz.interface';
import { Store, select } from '@ngrx/store';
import { QuizActions } from '../+state/quiz-app.actions';
import { Observable, Subscription, interval } from 'rxjs';
import { Router } from '@angular/router';
import {
  selectCurrentQuestion,
  selectCurrentQuestionNumber,
  selectQuizState,
  selectTotalQuestions,
} from '../+state/quiz-app.selectors';
import { QuizReduxService } from '../quiz-redux.service';

@Component({
  selector: 'quiz-app-quiz',
  templateUrl: './quiz.component.html',
  styleUrls: ['./quiz.component.scss'],
})
export class QuizComponent implements OnInit {
  constructor(
    private store: Store<Quiz>,
    private router: Router,
    private quizService: QuizReduxService
  ) {}

  quizStarted = false;
  currentQuestionNumber$!: Observable<number | null>;
  isOptionSelected = false;
  optionWindowVisible = false;
  sideWindowVisible = false;
  quizQuestions$!: Observable<Quiz>;
  selectCurrentQuestion$!: Observable<Question>;
  totalQuestions$!: Observable<number>;
  // nextBtn = 'Next';
  // set timer
  uiTimer: any;
  startTime: any;
  totalQuestions = 0;
  timerSubscription!: Subscription;
  timerDuration = 0;

  ngOnInit(): void {
    this.store.dispatch(QuizActions.loadQuizQuestions());
    this.selectCurrentQuestion$ = this.store.pipe(
      select(selectCurrentQuestion)
    );
    this.quizQuestions$ = this.store.select(selectQuizState);
    this.currentQuestionNumber$ = this.store.pipe(
      select(selectCurrentQuestionNumber)
    );
    this.totalQuestions$ = this.store.select(selectTotalQuestions);

    this.totalQuestions$.subscribe((totalQuestions: number) => {
      this.totalQuestions = totalQuestions;
      this.timerDuration = this.calculateTimerDuration();
      this.startTimer();
    });
  }

  calculateTimerDuration(): number {
    return this.totalQuestions * 10;
  }
  startTimer(): void {
    let timer = this.timerDuration;

    this.timerSubscription = interval(1000).subscribe(() => {
      if (timer <= 0) {
        const minutes = Math.floor(timer / 60);
        const seconds = timer % 60;

        const formattedMinutes = minutes < 10 ? '0' + minutes : '' + minutes;
        const formattedSeconds = seconds < 10 ? '0' + seconds : '' + seconds;

        this.uiTimer = `${formattedMinutes}:${formattedSeconds}`;
        timer--;
      } else {
        this.router.navigate(['/skip']);
      }
    });
  }

  openSideWindow() {
    this.sideWindowVisible = true;
    this.optionWindowVisible = false;
  }
  closeSideWindow() {
    this.sideWindowVisible = false;
  }
  toggleOptionWindow() {
    this.optionWindowVisible = !this.optionWindowVisible;
  }

  setCurrentQuestion(index: number) {
    this.store.dispatch(
      QuizActions.setCurrentQuestion({ currentQuestionNumber: index + 1 })
    );
  }

  // againLoadQuestions() {
  //   this.store.dispatch(QuizActions.loadQuizQuestions());
  // }

  nextQuestion(): void {
    this.store.dispatch(QuizActions.nextQuestion());
    this.isOptionSelected = false;
  }

  skipQuestion() {
    this.store.dispatch(QuizActions.skipQuestion());
  }

  handleOption(selectedOption: string) {
    this.store.dispatch(QuizActions.selectedOption({ selectedOption }));
    this.isOptionSelected = true;
  }

  // Restart quiz method
  // restartQuiz() {
  //   this.againLoadQuestions();
  //   this.store.dispatch(QuizActions.restartQuiz());
  // }
  previousQuestion() {
    this.store.dispatch(QuizActions.previousQuestion());
  }
}
<section *ngIf="quizQuestions$ | async as quizQuestions">
  <div class="side-btn">
    <button (click)="openSideWindow()">
      <i class="fa fa-angle-right"></i>
    </button>
  </div>
  <div (click)="closeSideWindow()">
    <div *ngIf="sideWindowVisible" class="side-window-container">
      <div class="side-window" (click)="$event.stopPropagation()">
        <div
          *ngFor="let question of quizQuestions.questions; let i = index"
          class="window-flex"
        >
          <h3 (click)="setCurrentQuestion(i)">
            {{ question.question.text }}
          </h3>
          <div class="attempted-question">
            <i class="fa fa-check-circle"></i>
          </div>
        </div>
      </div>
    </div>
  </div>

  <div class="nav" *ngIf="quizQuestions$ | async as quizQuestions">
    <div class="navbar">
      <button
        class="icon-button"
        (click)="previousQuestion()"
        [disabled]="quizQuestions.currentQuestionNumber === 1"
      >
        <i class="fa fa-angle-left"></i>
      </button>
      <button class="ques-btn">
        Question {{ quizQuestions.currentQuestionNumber }}/{{
          quizQuestions.totalQuestions
        }}
      </button>
      <button class="icon-button" (click)="nextQuestion()">
        <i class="fa fa-angle-right"></i>
      </button>

      <button class="option-panel" (click)="toggleOptionWindow()">
        <i class="fas fa-check-square"></i>
      </button>

      <div class="timer">{{ uiTimer }}</div>
    </div>
  </div>
  <div class="container">
    <div class="quiz-flex">
      <h3>
        {{ (selectCurrentQuestion$ | async)?.question?.text }}
      </h3>
      <ul>
        <li *ngFor="let option of quizQuestions.options; let i = index">
          {{ option }}
        </li>
      </ul>
    </div>
  </div>
  <div *ngIf="optionWindowVisible" class="option-window">
    <div class="radio-btns">
      <div *ngFor="let option of quizQuestions.options; let i = index">
        <input
          type="radio"
          type="radio"
          name="options"
          id="{{ 'option' + i }}"
          (click)="handleOption(option)"
          [disabled]="quizQuestions.response"
        />
        <label
          [ngClass]="{
            correct:
              quizQuestions.response && option === quizQuestions.correctAnswer,
            inCorrect:
              quizQuestions.response &&
              quizQuestions.response !== quizQuestions.correctAnswer
                ? option === quizQuestions.response
                : null
          }"
          for=""
          >{{ option }}</label
        >
      </div>
      <div class="btn-flex">
        <button
          class="next-btn"
          (click)="previousQuestion()"
          [disabled]="quizQuestions.currentQuestionNumber === 1"
        >
          Previous
        </button>
        <button class="next-btn" (click)="nextQuestion()">
          {{
            quizQuestions.currentQuestionNumber < quizQuestions.totalQuestions
              ? 'Next'
              : 'Finish'
          }}
        </button>
      </div>
    </div>
  </div>
</section>
