import { createFeatureSelector, createSelector } from '@ngrx/store';
import { QUIZ_APP_FEATURE_KEY } from './quiz-app.reducer';
import { Quiz } from '../quiz/quiz.interface';

export const selectQuizState =
  createFeatureSelector<Quiz>(QUIZ_APP_FEATURE_KEY);

// Selectors for individual pieces of state
export const selectQuestions = createSelector(
  selectQuizState,
  (state) => state.questions
);

export const selectCurrentQuestionIndex = createSelector(
  selectQuizState,
  (state) => state.current_Question_Index
);

export const selectTotalQuestions = createSelector(selectQuizState, (state) => {
  console.log('Total Questions:', state.questions.length);
  return state.questions.length;
});

export const selectCurrentQuestion = createSelector(
  selectQuestions,
  selectCurrentQuestionIndex,
  (questions, currentIndex) => questions[currentIndex]
);

export const selectCorrectAnswer = createSelector(
  selectCurrentQuestion,
  (currentQuestion) => currentQuestion?.correctAnswer
);

export const selectCurrentScore = createSelector(selectQuizState, (state) => {
  console.log('score', state.current_score);
  return state.current_score;
});
export const selectSelectedResponses = createSelector(
  selectQuizState,
  (state) => state.selectedResponses
);

export const selectSelectedOptionForCurrentQuestion = createSelector(
  selectSelectedResponses,
  selectCurrentQuestionIndex,
  (selectedResponses, currentIndex) => selectedResponses[currentIndex + 1] // Assuming question indices start from 1
);
