import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { Quiz } from './quiz/quiz.interface';

@Injectable({
  providedIn: 'root',
})
export class QuizReduxService {
  private apiUrl = 'https://the-trivia-api.com/api/questions';
  private apiKey = 'YOUR_API_KEY'; // Replace with your actual API key

  constructor(private http: HttpClient) {}

  getTriviaQuestion(): Observable<Quiz> {
    const apiUrlWithKey = `${this.apiUrl}?apiKey=${this.apiKey}&amount=1`;

    return this.http.get<Quiz>(apiUrlWithKey);
  }
}
