  // }
  loadTrivia$ = createEffect(() =>
    this.actions$.pipe(
      ofType(QuizActions.loadQuestions),
      mergeMap(() =>
        this.triviaService.getTriviaQuestion().pipe(
          map((trivia: any) => {
            console.log('Trivia questions received from Effects:', trivia);
            const questions: Question[] = trivia.map((questionItem: any) => ({
              question: questionItem.question.text,
              options: this.shuffleArray([
                ...questionItem.incorrectAnswers,
                questionItem.correctAnswer,
              ]),
              correctAnswer: questionItem.correctAnswer,
            }));
            return QuizActions.loadQuestionsSuccess({ questions });
          }),
          catchError((error) => {
            console.error('Error in loadTodos effect:', error);
            return of(QuizActions.loadQuestionsFailure({ error }));
          })
        )
      )
    )
  );
  shuffleArray(array: string[]): string[] {
    return array.sort(() => Math.random() - 0.6);
  }
}
